mapper.insert:
insert into t_homework(
                       homework_id, teacher_id, homework_name, content, picture, file, school, grade, classes, deadline
) VALUES (
          #{homeworkId},#{teacherId},#{homeworkName},#{content},#{picture},#{file},#{school},#{grade},#{classes},#{deadline}
                 )

mapper.update:
update t_student_submission set
                                status = 'corrected',
                                level = #{level},
                                feedback = #{feedback}
where submission_id = #{submissionId}

mapper.insert:
insert into t_student_submission(
                                 submission_id, homework_id, student_id, student_name, submission_time, content, file, picture, sound, status, level, feedback
) VALUES (
          #{submissionId},#{homeworkId},#{studentId},#{studentName},#{submissionTime},#{content},#{file},#{picture},#{sound},'uncorrected',null,null
                 )
