mapper.insert:
insert into t_course_video(
    video_id, video_name, status, create_time, create_user, cover_url, video_url, introduction
) VALUES (
    #{videoId},#{videoName},#{status},#{createTime},#{createUser},#{coverUrl},#{videoUrl},#{introduction}
                  )

mapper.update:
update t_course_video
video_id = #{videoId}, video_name = #{videoName}, status = #{status}, create_time = #{createTime}, create_user = #{createUser}, cover_url = #{coverUrl}, video_url = #{videoUrl}, introduction = #{introduction},
video_name = #{videoName}

